function charBox(size) {
	if (size === 0) {
		return [[]];
	}

	if (Number.isInteger(size) && size > 0) {
		return Array.from({ length: size }, (_, i) =>
			i === 0 || i === size - 1
				? '#'.repeat(size).split('')
				: `#${' '.repeat(size - 2)}#`.split('')
		);
	}
	return -1;
}

