function isCheckerboard(arr) {
  let col = [];
  for (let i = 0; i < arr[0].length; i++) {
    col[i] = new Array(arr.length).fill();

    for (let j = 0; j < arr.length; j++) {
      col[i][j] = arr[j][i];
    }
  }

  let rowMuster = arr.some(x => x.join('').match(/(.)\1/g));
  let colMuster = col.some(x => x.join('').match(/(.)\1/g));

  return !rowMuster || !colMuster;
}

