function decomposeAddress(str) {

  let streetNumber = str.match(/^\d*/g);
  let streetName = str.match(/\b[A-Z]*\s\b([A-Z][a-z])\b/gi);
  let cityName = str.match(/(([A-Z][a-z]){1}\s)(.)*(?=,)/g)[0].split(' ').filter((x,i) => i > 0 ? x:false).join(' ');
  let state = str.match(/[A-Z]{2}/g);
  let zipCode = str.match(/\d{5}$/g);

  let arr = [streetNumber, streetName, cityName, state, zipCode];
  return [].concat(...arr);

}

