function isPowerful(num) {

  let num1 = num;
  let factors = [];

  for (let i = 2; i <= num1; i++) {
    while ((num1 % i) === 0) {
      factors.push(i);
      num1 /= i;
    }
  }

  return Array.from(new Set(factors)).every(x => num % (x * x) === 0);

}

